-- Verificar domain
SELECT 
  id, 
  domain,
  neurocore_id,
  active
FROM public.knowledge_domains 
WHERE domain = 'exames';

-- Contar bases criadas
SELECT 
  COUNT(*) as total_bases
FROM public.base_conhecimentos
WHERE tenant_id = '004ead16-af22-419c-8e19-28966d4c4d38'
  AND domain IN (SELECT id FROM public.knowledge_domains WHERE domain = 'exames');

-- Listar primeiras bases
SELECT 
  name, 
  is_active,
  LEFT(description, 80) as description_preview,
  created_at
FROM public.base_conhecimentos
WHERE tenant_id = '004ead16-af22-419c-8e19-28966d4c4d38'
  AND domain IN (SELECT id FROM public.knowledge_domains WHERE domain = 'exames')
ORDER BY created_at DESC
LIMIT 10;
